INSERT INTO `PREFIX_DB_category_description` (`category_id`, `language_id`, `name`, `description`, `meta_title`, `meta_h1`, `meta_description`, `meta_keyword`) VALUES ('87', '1', 'Смарт-часы', '&lt;p align=&quot;justify&quot;&gt;Без мобильного телефона с нашим бешеным темпом жизни сложно обойтись. Чтобы везде успеть, все помнить и быть всегда на связи, достаточно приобрести хотя бы бюджетную модель. Мобильные телефоны оснащены набором всех необходимых человеку функций. Это будильник, календарь с планировщиком, мультимедийный проигрыватель, радио, различные приложения, таймер, калькулятор, конвертер. Некоторые модели заменяют современный зеркальный фотоаппарат и видеокамеру, а так же &amp;nbsp;имеют возможность выхода в интернет (хотя, конечно, полного доступа ко всем возможностям Сети, как смартфон, они не дают). &lt;br&gt;&lt;/p&gt;', '', '', '', '');
