INSERT INTO `PREFIX_DB_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_h1`, `meta_description`, `meta_keyword`) VALUES ('59', '1', 'Тюнер цифрового ТВ Trimax TR-2012HD PVR', '&lt;p&gt;Как известно, цифровое телерадиовещание в Украине ведется в двух стандартах – DVB-T и DVB-T2, и именно второй сейчас представляется более перспективным. Учитывая, что в 2015 году Украина собирается отказаться от аналогового вещания, пользователям, которые не желают связываться ни со спутниковым, ни с кабельным телевидением и предпочитают и дальше пользоваться эфирным, стоит задуматься об апгрейде своего телевизора уже сейчас. Как известно, сигнал DVB-T2 в Украине закодирован, и принимать его могут лишь несколько сертифицированных ресиверов или CAM-модулей. С одним из таких приемников мы и решили познакомиться поближе.&lt;/p&gt;', '', '', '', '', '');
